        return new Source("{% extends 'base.html.twig' %}

{% block title %} Contact !{% endblock %}

{% block body %}
    
    <div class=\"container mt-5 pt-5\">

        <h1>{{ 'page.contact.title'|trans }}</h1>
        <p>{{ 'page.contact.subtitle'|trans }}</p>

        <form class=\"mt-4\">
            
            <div class=\"form-group\">
                <label for=\"exampleFormControlInput1\">Votre Adresse</label>
                <input type=\"email\" class=\"form-control\" id=\"exampleFormControlInput1\" placeholder=\"name@example.com\">
            </div>
            
            <div class=\"form-group\">
                <label for=\"exampleFormControlTextarea1\">Votre texte</label>
                <textarea class=\"form-control\" id=\"exampleFormControlTextarea1\" rows=\"10\"></textarea>
            </div>
            
            <div class=\"text-right\">
                <button type=\"submit\" class=\"btn btn-primary\">Envoyer</button>
            </div>
        
        </form>

        <div class=\"mt-5\">
            Pour toute demande de <b>contact professionnelle</b>,
